mapper.select:
select T1.id, T1.phone_number, T1.zip_code, T1.si_do, T1.si_gun_gu, T1.eup_myeon_dong, T1.address, T2.vaccine_id, T2.rest_amount
from (select * from agency where 5000 >= st_distance_sphere(POINT(lng,lat), POINT(#{lookupRequest.lng},#{lookupRequest.lat})))
as T1 inner join (select * from acquired_vaccine where

    #{nextDay} > vaccinate_at and vaccinate_at >= #{lookupRequest.date}


    now() >= vaccinate_at


    and vaccine_id = #{code}


    and rest_amount > 0

 ) as T2 on T1.id = T2.agency_id

select b.vaccinate_at, b.acquired_vaccine_id, c.name, c.phone_number, c.zip_code,
c.si_do, c.si_gun_gu, c.eup_myeon_dong, c.address from booking as b inner join acquired_vaccine as a on b.acquired_vaccine_id = a.id and b.user_id = #{userId}
inner join agency c on a.agency_id = c.id
